import React from "react";
import { makeStyles, useTheme } from "@mui/styles";
import Button from "@mui/material/Button";
import { Divider, Typography } from "@mui/material";
import { Add } from "@mui/icons-material";
import CustomDialog from "../../../../components/dashboard/dialogs/custom-dialog";
import CategoryForm from "../../../../forms/category/new_category_form";
import Card from "@mui/material/Card";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import IconButton from "@mui/material/IconButton";
import { Edit } from "@mui/icons-material";
import { Delete } from "@mui/icons-material";
import { display } from "@mui/system";

import {
  onSnapshot,
  query,
  collection,
  db,
  where,
} from "../../../../../data/firebase/";

const useStyles = makeStyles((theme) => ({
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  main: {
    display: "flex",
    flex: 1,
    flexDirection: "column",
    margin: "auto",
    minHeight: 256,
    alignItems: "center",
  },
  cardMedia: {
    height: 156,
    width: 200,
  },
  subRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "end",
    alignItems: "center",
  },
}));

const CategoryItem = (props) => {
  const { image, name } = props;
  const classes = useStyles();

  return (
    <Card elevation={3}>
      <CardContent>
        <CardMedia image={image} className={classes.cardMedia} />
        <Divider />
        <div className={classes.row}>
          <Typography>{name}</Typography>
          <div className={classes.subRow}>
            <Button startIcon={<Edit />} size="small">
              Edit
            </Button>
            <Button startIcon={<Delete />} size="small">
              Delete
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

const Categories = () => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  React.useEffect(() => {
    const q = query(collection(db, "cities"), where("state", "==", "CA"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const cities = [];
      querySnapshot.forEach((doc) => {
        cities.push(doc.data().name);
      });
      console.log("Current cities in CA: ", cities.join(", "));
    });
  }, []);

  return (
    <div>
      <CustomDialog
        open={open}
        title="Create New Category"
        handleClose={() => setOpen(false)}
        bodyComponent={<CategoryForm setOpen={setOpen} />}
      />
      <div className={classes.row}>
        <Typography variant="h6" color="blue" fontSize={21}>
          Categories
        </Typography>
        <Button
          startIcon={<Add />}
          color="primary"
          variant="contained"
          onClick={() => setOpen(true)}
        >
          Add Category
        </Button>
      </div>
      <div className={classes.main}></div>
    </div>
  );
};

export default Categories;
