import { Card, Divider, Grid, Typography } from "@mui/material";
import React from "react";
import { useMediaQuery } from "@mui/material";
import { useTheme } from "@mui/material";
import { makeStyles } from "@mui/styles";
import {
  onSnapshot,
  query,
  collection,
  db,
} from "../../../../../data/firebase";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    height: "100vh",
  },
  button: {
    textTransform: "none",
    fontSize: 11,
  },
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
}));

const HomePage = () => {
  const classes = useStyles();
  const theme = useTheme();
  const small = useMediaQuery(theme.breakpoints.only("xs"));
  const medium = useMediaQuery(theme.breakpoints.only("sm"));

  const [projectsList, setProjectsList] = React.useState(null);
  const [newsList, setNewsList] = React.useState(null);

  React.useEffect(() => {
    const q = query(collection(db, "news"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const news = [];
      querySnapshot.forEach((doc) => {
        news.push(doc.data());
      });
      setNewsList(news);
    });
  }, []);

  React.useEffect(() => {
    const q = query(collection(db, "projects"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const proj = [];
      querySnapshot.forEach((doc) => {
        proj.push(doc.data());
      });
      setProjectsList(proj);
    });
  }, []);

  return (
    <div>
      <Grid container spacing={1}>
        <Grid xs={12} sm={7} md={6}></Grid>

        <Grid xs={12} sm={5} md={6}>
          <Card sx={{ mb: 3, mt: 3 }}>
            <div className={classes.row} style={{ padding: 10 }}>
              <Typography>Total categories</Typography>
              <Typography>{}</Typography>
            </div>
            <Divider color="primary" />
            <div className={classes.row} style={{ padding: 10 }}>
              <Typography>Total news feeds</Typography>
              <Typography>{newsList?.length}</Typography>
            </div>
            <Divider color="primary" />
            <div className={classes.row} style={{ padding: 10 }}>
              <Typography>Total projects</Typography>
              <Typography>{newsList?.length}</Typography>
            </div>
          </Card>
        </Grid>
      </Grid>
    </div>
  );
};

export default HomePage;
