import React from "react";
import { makeStyles } from "@mui/styles";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import { ArrowBackIosNew, Edit } from "@mui/icons-material";
import image from "../../../../../../assets/images/privacy.jpeg";
import Grid from "@mui/material/Grid";
import { useHistory } from "react-router-dom";
import {
  onSnapshot,
  query,
  collection,
  db,
  doc,
  ref,
  updateDoc,
} from "../../../../../../data/firebase";
import CustomDialog from "../../../../../components/dashboard/dialogs/custom-dialog";
import UpdatePolicyForm from "../../../../../forms/privacy-policy/update_policy_form";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "stretch",
  },
  header: {
    height: 286,
    width: "100%",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
    backgroundPosition: "center",
  },
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
}));

const PrivacyPolicy = () => {
  const classes = useStyles();
  const history = useHistory();
  const [open, setOpen] = React.useState(false);
  const [privacyData, setPrivacyData] = React.useState(null);
  let id = "";

  React.useEffect(() => {
    const unsub = onSnapshot(doc(db, "others", "privacy-policy"), (doc) => {
      console.log("Current data: ", doc.data());
      setPrivacyData(doc.data());
    });
  }, []);

  return (
    <div>
      <CustomDialog
        title="Update Privacy Policy"
        open={open}
        handleClose={() => setOpen(false)}
        bodyComponent={<UpdatePolicyForm setOpen={setOpen} />}
      />
      <div className={classes.row}>
        <Button
          variant="text"
          startIcon={<ArrowBackIosNew />}
          onClick={() => history.goBack()}
        >
          Back
        </Button>
        <Typography variant="h5" color="goldenrod">
          Privacy Policy
        </Typography>
        <Button startIcon={<Edit />} variant="contained">
          Edit
        </Button>
      </div>
      <br />
      <Grid container spacing={1}>
        <Grid item xs={12} sm={6} md={7}>
          {privacyData && (
            <>
              <Typography gutterBottom align="left" fontSize={13}>
                Last updated on{" "}
              </Typography>
              <Typography align="justify">{privacyData?.body}</Typography>
            </>
          )}
        </Grid>

        <Grid item xs={12} sm={6} md={5}>
          <div>
            <img src={image} alt="featured-img" width="100%" />
          </div>
        </Grid>
      </Grid>
    </div>
  );
};

export default PrivacyPolicy;
