import React from "react";
import { makeStyles, useTheme } from "@mui/styles";
import Button from "@mui/material/Button";
import { Typography } from "@mui/material";
import { Add } from "@mui/icons-material";
import CustomDialog from "../../../../components/dashboard/dialogs/custom-dialog";
import CategoryForm from "../../../../forms/category/new_category_form";

const useStyles = makeStyles((theme) => ({
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
}));

const Categories = () => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  return (
    <div>
      <CustomDialog
        open={open}
        title="Craete new category"
        handleClose={() => setOpen(false)}
        bodyComponent={<CategoryForm />}
      />
      <div className={classes.row}>
        <Typography variant="h6" color="blue" fontSize={21}>
          Categories
        </Typography>
        <Button
          startIcon={<Add />}
          color="primary"
          variant="contained"
          onClick={() => setOpen(true)}
        >
          Add Category
        </Button>
      </div>
    </div>
  );
};

export default Categories;
