import {
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Typography,
} from "@mui/material";
import React from "react";
import { makeStyles, useTheme } from "@mui/styles";
import categoryImage from "../../../../../assets/images/category_ndua_admin.jpeg";
import directoryImge from "../../../../../assets/images/directories_ndua_admin.jpeg";
import newsImage from "../../../../../assets/images/news_ndua_admin.jpeg";
// import vendorsLogo from "../../../../../assets/images/vendors_ndua_admin.jpeg";
import projectsImage from "../../../../../assets/images/projects_ndua_admin.jpeg";
import adsImage from "../../../../../assets/images/ads_ndua_admin.jpeg";
import Grid from "@mui/material/Grid";

const useStyles = makeStyles((theme) => ({
  root: {},
  image: {
    height: 144,
  },
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  column: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "start",
    alignItems: "center",
  },
}));

const CardItem = (props) => {
  const classes = useStyles();
  const { image, title, excerpt, to } = props;

  return (
    <Card elevation={2}>
      <CardContent>
        <CardMedia image={image} className={classes.image} />
        <div className={classes.row}>
          <div className={classes.column}>
            <Typography>{title}</Typography>
            <Typography>{excerpt}</Typography>
          </div>
          <CardActions onClick={() => {}}>View All</CardActions>
        </div>
      </CardContent>
    </Card>
  );
};

const ManageApp = () => {
  let list = [
    {
      title: "Categories",
      desc: "View/Manage app categories",
      to: "/categories",
      image: categoryImage,
    },
    {
      title: "News Feeds",
      desc: "View/Manage app news feeds",
      to: "/news-feeds",
      image: newsImage,
    },
    {
      title: "Projects",
      desc: "View/Manage app projects",
      to: "/projects",
      image: projectsImage,
    },
    {
      title: "Directories",
      desc: "View/Manage app directories",
      to: "/directories",
      image: directoryImge,
    },
    {
      title: "Ads Center",
      desc: "View/Manage adverts",
      to: "/ads-center",
      image: adsImage,
    },
  ];

  return (
    <div>
      <Grid
        container
        spacing={{ xs: 2, md: 3 }}
        columns={{ xs: 4, sm: 8, md: 12 }}
      >
        {list?.map((_, index) => (
          <Grid item xs={2} sm={4} md={4} key={index}>
            <CardItem
              image={list[index].image}
              title={list[index].title}
              excerpt={list[index].desc}
              to={list[index].to}
            />
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default ManageApp;
