import React from "react";
import { Button, IconButton, Typography } from "@mui/material";
import { CircularProgress } from "@mui/material";
import {
  TextValidator,
  SelectValidator,
} from "react-material-ui-form-validator";
import { useSnackbar } from "notistack";
import {
  db,
  query,
  collection,
  onSnapshot,
} from "../../../../../data/firebase/";
import MenuItem from "@mui/material/MenuItem";
import { withRouter } from "react-router-dom";
import { makeStyles } from "@mui/styles";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";

const useStyles = makeStyles((theme) => ({
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  main: {
    display: "flex",
    flex: 1,
    flexDirection: "column",
    margin: "auto",
    minHeight: 256,
    minWidth: 320,
    alignItems: "center",
  },
  cardMedia: {
    height: 156,
    width: "100%",
  },
  subRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "end",
    alignItems: "center",
  },
  lhsRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "start",
    alignItems: "center",
  },
  avatar: {
    height: 36,
    width: 36,
  },
  column: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "start",
    alignItems: "start",
    padding: 4,
  },
  mb: {
    marginBottom: 10,
  },
}));

const NewsItem = (props) => {
  const { history, location } = props;
  const classes = useStyles();

  const [formValues, setFormValues] = React.useState({
    title: "",
    image: "",
    category: "",
    subTitle: "",
    body: "",
    createdAt: "",
    authorName: "",
    authorPhoto: "",
  });
  const [file, setFile] = React.useState(null);
  const [authorFile, setAuthorFile] = React.useState(null);
  const [isUploading, setIsUploading] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(false);
  const [progress, setProgress] = React.useState(0);
  const [previewImage, setPreviewImage] = React.useState("");
  const [previewAuthor, setPreviewAuthor] = React.useState("");
  const { enqueueSnackbar } = useSnackbar();

  const [saving, setSaving] = React.useState(false);
  const [shouldEditTitle, setShouldEditTitle] = React.useState(false);
  const [shouldEditSubtitle, setShouldEditSubtitle] = React.useState(false);
  const [shouldEditCategory, setShouldEditCategory] = React.useState(false);
  const [shouldEditAuthor, setShouldEditAuthor] = React.useState(false);
  const [shouldEditContent, setShouldEditContent] = React.useState(false);
  const [shouldEditImage, setShouldEditImage] = React.useState(false);
  const [shouldEditAuthorPic, setShouldEditAuthorPic] = React.useState(false);
  const [categoriesList, setCategoriesList] = React.useState(null);

  React.useEffect(() => {
    const q = query(collection(db, "categories"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const categories = [];
      querySnapshot.forEach((doc) => {
        categories.push(doc.data());
      });
      setCategoriesList(categories);
    });
  }, []);

  const handleChange = (e) => {
    const { id, name, value } = e.target;

    if (id === "image") {
      setFile(e.target.files[0]);
      setPreviewImage(URL.createObjectURL(e.target.files[0]));
      setFormValues((prevData) => ({
        ...prevData,
        image: e.target.value,
      }));
    } else if (id === "authorPhoto") {
      setAuthorFile(e.target.files[0]);
      setPreviewAuthor(URL.createObjectURL(e.target.files[0]));
      setFormValues((prevData) => ({
        ...prevData,
        authorPhoto: e.target.value,
      }));
    } else {
      setFormValues((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  return (
    <div>
      <div className={classes.row}>
        <Button startIcon={<ArrowBackIosNewIcon />}>Back</Button>
        <div className={classes.lhsRow}>
          {shouldEditTitle ? (
            <TextValidator
              className={classes.mb}
              id="title"
              label="News title"
              size="small"
              variant="outlined"
              value={formValues.subTitle}
              onChange={handleChange}
              name="subTitle"
              fullWidth
            />
          ) : (
            <div className={classes.lhsRow}>
              <Typography fontSize={21} variant="h6" textAlign="center">
                {location?.state?.title}
              </Typography>
              <IconButton aria-label="delete">
                <DeleteIcon />
              </IconButton>
            </div>
          )}
        </div>

        <Button
          variant="contained"
          onClick={() => {}}
          endIcon={<CircularProgress />}
        >
          Update
        </Button>
      </div>
      <br />
      {/* Image Section */}
      <div>
        <img
          src={location?.state?.image}
          alt="featured_image"
          width="100%"
          height={400}
        />
      </div>

      <div>
        {shouldEditCategory ? (
          <SelectValidator
            className={classes.mb}
            value={formValues.category}
            onChange={handleChange}
            label="News category"
            name="category"
            variant="outlined"
            size="small"
            validators={["required"]}
            errorMessages={["News category is required"]}
          >
            {(categoriesList ?? [])?.map((item, index) => (
              <MenuItem key={index} value={item?.title ?? ""}>
                {item?.title ?? ""}
              </MenuItem>
            ))}
          </SelectValidator>
        ) : (
          <Typography fontSize={21} variant="h6">
            {location?.state?.category}
          </Typography>
        )}
      </div>
    </div>
  );
};

export default withRouter(NewsItem);
