import React from "react";
import { Avatar, Button, Typography } from "@mui/material";
import { withRouter } from "react-router-dom";
import { makeStyles } from "@mui/styles";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import { IconButton } from "@mui/material";
import CustomDialog from "../../../../../components/dashboard/dialogs/custom-dialog";
import DeleteDialog from "../../../../../components/dashboard/dialogs/custom-dialog";
import EditNewsForm from "../../../../../forms/news/update_news_form";
import { useSnackbar } from "notistack";
import {
  deleteDoc,
  deleteObject,
  ref,
  db,
  doc,
  storage,
} from "../../../../../../data/firebase";
import Delete from "@mui/icons-material/Delete";
import Edit from "@mui/icons-material/Edit";
import { Add } from "@mui/icons-material";

const useStyles = makeStyles((theme) => ({
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  main: {
    display: "flex",
    flex: 1,
    flexDirection: "column",
    margin: "auto",
    minHeight: 256,
    minWidth: 320,
    alignItems: "center",
  },
  cardMedia: {
    height: 156,
    width: "100%",
  },
  subRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "end",
    alignItems: "center",
  },
  lhsRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "start",
    alignItems: "center",
  },
  avatar: {
    height: 48,
    width: 48,
  },
  column: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "start",
    alignItems: "start",
    padding: 4,
  },
  mb: {
    marginBottom: 10,
  },
}));

const VendorItem = (props) => {
  const { history, location } = props;
  const classes = useStyles();

  const [open, setOpen] = React.useState(false);
  const [openEdit, setOpenEdit] = React.useState(false);
  const [openDelete, setOpenDelete] = React.useState(false);
  const { enqueueSnackbar } = useSnackbar();

  const deleteVendor = () => {
    setOpenDelete(false);
    const fileRef = ref(storage, "news/" + location?.state?.id);
    const fileRef2 = ref(storage, "news/img_" + location?.state?.id);

    deleteObject(fileRef)
      .then(() => {
        deleteObject(fileRef2)
          .then(async () => {
            // Images deleted now delete from firestore,
            try {
              await deleteDoc(doc(db, "news", "" + location?.state?.id));
              enqueueSnackbar(`Item deleted successfully`, {
                variant: "success",
              });
            } catch (error) {
              console.log("ERR: Del: ", error);
              enqueueSnackbar(`Item not deleted. Try again`, {
                variant: "error",
              });
            }
          })
          .catch((err) => {});
      })
      .catch((error) => {
        console.log("ErR: ", error);
      });
  };

  const deleteBody = (
    <div>
      <Typography variant="body2" gutterBottom>
        {`Are you sure you want to delete ${location?.state?.title} ?`}
      </Typography>
      <br />
      <div className={classes.subRow}>
        <Button
          size="small"
          variant="contained"
          style={{ marginRight: 4 }}
          onClick={() => setOpenDelete(false)}
        >
          Cancel
        </Button>

        <Button
          size="small"
          variant="contained"
          color="error"
          onClick={deleteVendor}
        >
          Delete
        </Button>
      </div>
    </div>
  );

  return (
    <>
      <CustomDialog
        open={open}
        title="Update News"
        handleClose={() => setOpen(false)}
        bodyComponent={
          <EditNewsForm
            setOpen={setOpen}
            img={location?.state?.image}
            name={location?.state?.name}
            id={location?.state?.id}
            address={location?.state?.address}
            phone={location?.state?.phone}
            website={location?.state?.website}
            description={location?.state?.description}
            category={location?.state?.category}
            createdAt={location?.state?.createdAt}
            updatedAt={location?.state?.updatedAt}
            logo={location?.state?.logo}
            is24hrs={location?.state?.is24hrs}
            opensAt={location?.state?.opensAt}
            closesAt={location?.state?.closesAt}
          />
        }
      />
      <DeleteDialog
        open={openDelete}
        title="Delete Vendor"
        handleClose={() => setOpenDelete(false)}
        bodyComponent={deleteBody}
      />
      <div className={classes.row}>
        <Button
          startIcon={<ArrowBackIosNewIcon />}
          onClick={() => history.goBack()}
        >
          Back
        </Button>
        <div className={classes.lhsRow}>
          <Typography
            fontSize={21}
            variant="h6"
            textAlign="center"
            fontWeight="bold"
          >
            {location?.state?.name}
          </Typography>
        </div>

        <Button
          startIcon={<Add />}
          variant="contained"
          onClick={() => setOpen(true)}
        >
          Add Product
        </Button>
      </div>
      <br />
      {/* Image Section */}
      <div>
        <img
          src={location?.state?.image}
          alt="featured_image"
          width="100%"
          height={400}
        />
      </div>
      <br />

      <div className={classes.row}>
        <Typography fontSize={16} variant="h6" textAlign="start" gutterBottom>
          {location?.state?.category}
        </Typography>

        <div className={classes.lhsRow}>
          <IconButton
            aria-label="delete"
            color="primary"
            onClick={() => setOpen(true)}
          >
            <Edit />
          </IconButton>
          <IconButton
            aria-label="delete"
            color="error"
            onClick={() => setOpenDelete(true)}
          >
            <Delete />
          </IconButton>
        </div>
      </div>

      <Typography fontSize={18} variant="h6" textAlign="start">
        {location?.state?.address}
      </Typography>
      <br />

      <div className={classes.lhsRow}>
        <Typography fontSize={15} variant="h6" textAlign="start">
          {location?.state?.description}
        </Typography>
      </div>
      <br />

      <div className={classes.row}>
        <div>
          <Avatar
            alt="Brand-Logo"
            src={location?.state?.logo}
            className={classes.avatar}
          />
          <Typography
            fontSize={16}
            fontWeight="bold"
            textAlign="start"
            gutterBottom
          >
            {location?.state?.phone}
          </Typography>
          <Typography
            fontSize={16}
            fontWeight="bold"
            textAlign="start"
            gutterBottom
          >
            {location?.state?.website}
          </Typography>
        </div>

        <div>
          {location?.state?.is24hrs ? (
            <Typography>Always open 24/7</Typography>
          ) : (
            <Typography></Typography>
          )}
          <div></div>
        </div>
      </div>
    </>
  );
};

export default withRouter(VendorItem);
