import {
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Grid,
  Typography,
} from "@mui/material";
import React from "react";
import { useMediaQuery } from "@mui/material";
import { useTheme } from "@mui/material";
import PersonOutline from "@mui/icons-material/PersonOutline";

const HomePage = () => {
  const theme = useTheme();
  const small = useMediaQuery(theme.breakpoints.only("xs"));
  const medium = useMediaQuery(theme.breakpoints.only("sm"));

  if (small) {
    return (
      <div>
        <Grid container spacing={1}>
          <Grid xs={12}>
            <Card>
              <CardActionArea>
                <CardContent>
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      justifyContent: "center",
                      alignItems: "center",
                    }}
                  >
                    <PersonOutline style={{ width: 100, height: 128 }} />
                    <PersonOutline style={{ width: 100, height: 128 }} />
                  </div>
                </CardContent>
                <CardActions>
                  <Typography
                    display="flex"
                    flexDirection="center"
                    justifyContent="center"
                    alignSelf="center"
                  >
                    View all users
                  </Typography>
                </CardActions>
              </CardActionArea>
            </Card>
          </Grid>
          <Grid xs={12}></Grid>
        </Grid>
      </div>
    );
  } else if (medium) {
    return (
      <div>
        <Grid container spacing={1}>
          <Grid sm={6}></Grid>

          <Grid sm={6}></Grid>
        </Grid>
      </div>
    );
  } else {
    return (
      <div>
        <Grid container spacing={1}>
          <Grid sm={4}></Grid>
          <Grid sm={4}></Grid>
          <Grid sm={4}></Grid>
        </Grid>
      </div>
    );
  }
};

export default HomePage;
