import React from "react";
import { Button, Typography } from "@mui/material";
import { withRouter } from "react-router-dom";
import { makeStyles } from "@mui/styles";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";

const useStyles = makeStyles((theme) => ({
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  main: {
    display: "flex",
    flex: 1,
    flexDirection: "column",
    margin: "auto",
    minHeight: 256,
    minWidth: 320,
    alignItems: "center",
  },
  cardMedia: {
    height: 156,
    width: "100%",
  },
  subRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "end",
    alignItems: "center",
  },
  lhsRow: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "start",
    alignItems: "center",
  },
  avatar: {
    height: 36,
    width: 36,
  },
  column: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "start",
    alignItems: "start",
    padding: 4,
  },
  mb: {
    marginBottom: 10,
  },
}));

const NewsItem = (props) => {
  const { history, location } = props;
  const classes = useStyles();

  return (
    <>
      <div className={classes.row}>
        <Button
          startIcon={<ArrowBackIosNewIcon />}
          onClick={() => history.goBack()}
        >
          Back
        </Button>
        <div className={classes.lhsRow}>
          <Typography fontSize={21} variant="h6" textAlign="center">
            {location?.state?.title}
          </Typography>
        </div>

        <Button variant="contained" onClick={() => {}}>
          Update
        </Button>
      </div>
      <br />
      {/* Image Section */}
      <div>
        <img
          src={location?.state?.image}
          alt="featured_image"
          width="100%"
          height={400}
        />
      </div>
      <br />

      <Typography fontSize={16} variant="h6" textAlign="start" gutterBottom>
        {location?.state?.category}
      </Typography>
      <Typography fontSize={18} variant="h6" textAlign="start">
        {location?.state?.subTitle}
      </Typography>
      <br />

      <div className={classes.lhsRow}>
        <Typography fontSize={16} variant="h6" textAlign="start">
          {location?.state?.body}
        </Typography>
      </div>
      <br />

      <div>
        <Typography fontSize={21} fontWeight="bold" textAlign="start">
          Author
        </Typography>
      </div>
    </>
  );
};

export default withRouter(NewsItem);
